def process_grid(input_text):
    # Split into rows
    rows = input_text.strip().split('\n')
    
    # Process each row
    decremented_rows = []
    for row in rows:
        # Split into numbers, filter out empty strings, convert to int, decrement, and join back
        numbers = [str(int(x) - 1) for x in row.split(',') if x.strip()]
        # Keep the same number of commas as the original
        formatted_row = ','.join(numbers)
        if row.endswith(','):
            formatted_row += ','
        decremented_rows.append(formatted_row)
    
    # Join rows back together, preserving original line endings
    return '\n'.join(decremented_rows)

